import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../_services/auth.service';
import { TokenStorageService } from '../_services/token-storage.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit{
  form: any = {
    name: null,
    email: null,
    password: null,
    phone_number: null,
    cumn: null,
    newsletter: false,
    // news: null,
  };

  currentUser: any;
  // news = "";

  isSuccessful = false;
  isSignUpFailed = false;
  errorMessage = '';
  constructor(private authService: AuthService, private token: TokenStorageService, private router: Router) { }

  ngOnInit(): void {
    this.currentUser = this.token.getUser();
    const checkbox = document.getElementById('news',) as HTMLInputElement;
    console.log(checkbox);
    if(this.currentUser.newsletter == 1){
      checkbox.checked = true;
    }
    console.log(checkbox.checked);
    // if(this.currentUser.success.newsletter == 1){
    //   this.news = "YES";
    // }else{
    //   this.news = "NO";
    // }
  }

  onSubmit(): void {
    const { name, email, password, phone_number, cumn, newsletter  } = this.form;

    // console.log(this.form);
    this.authService.register(name, email, password, phone_number, cumn, newsletter).subscribe(
      data => {
        // console.log(data);
        this.isSuccessful = true;
        this.isSignUpFailed = false;
        this.router.navigate(['profile']); 
      },
      err => {
        this.errorMessage = err.error.message;
        this.isSignUpFailed = true;
      }
    );
  }

}
